pub mod iterative;
pub mod iterative_2;

pub trait Solution {
    fn num_different_integers(word: String) -> i32;
}

#[cfg(test)]
mod tests {
    use super::Solution;

    pub fn run<S: Solution>() {
        const LONG_STR_1: &str = "xtimt5kqkz9osexe56ezwwninlyeeqsq5m99904os3ygs12t31n1et4uwzmt5kvv6teisobuxt10k33v1aaxysg4y8nsivxdp3fo9dr7x58m8uc4ofm41ai77u8cvzr5r3s97f5otns59ubqk57xwl00xsp9w2oodt6yxcbscloyr9c2su8gca1ly6rrjufm25luhxhesxwn7bk1as9na4cbabxk";
        const LONG_STR_2: &str = "";

        let test_cases = [
            ("a123bc34d8ef34", 3),
            ("leet1234code234", 2),
            ("a1b01c001", 1),
            ("0a0", 1),
            (LONG_STR_1, 23),
            ("0i00e", 1),
            (LONG_STR_2, 3),
        ];

        for (word, expected) in test_cases {
            assert_eq!(S::num_different_integers(word.to_string()), expected);
        }
    }
}
